<template>
    <div class="seccion">
        <div class="atributo">
            <span>{{nombreCompleto}}</span>
        </div>
        <div class="atributo">
            <!--v-bind:src es lo mismo que :src -->
            <img v-bind:src="foto_perfil" alt="">
        </div>
        <div class="atributo">
            <a :href="biografia" target="_blank">Biograf√≠a</a>
        </div>
        <div class="atributo">
            <span>Edad: {{edad}}</span>
        </div>
        <div class="atributo">
            <span>
                {{email}}
            </span>
        </div>
    </div>
</template>

<script>
export default {
    props:['email'],
    data(){
        return{
            nombres: 'Isaac',
            apellidos: 'Newton',
            fecha_nacimiento: new Date(1994,9,27),
            foto_perfil:'data:image/jpeg;base64,',
            biografia:'https://es.wikipedia.org/wiki/Isaac_Newton'
        }
    },
    computed: {
        nombreCompleto(){
            return this.nombres + ' ' + this.apellidos;
        },
        edad(){
            return this.calcularEdad(this.fecha_nacimiento);
        }
    },
    methods: {
        calcularEdad(fecha){
            let fecha_actual = new Date();
            let diferencia = fecha_actual - this.fecha_nacimiento;
            return Math.floor(diferencia / (1000*60*60*24*365 ));
        }
    },
    created() {
        this.$emit('onCambioEmail','newton@email.com')
    },
}
</script>

